    private string horizSelection = "0000000000000000000000000000000000000000";
    private string vertSelection = "000000000000000000000000000000000000000000";

    private int sol = 0;

    private Coroutine runner;
    private double timeHeld;

    static int moduleIdCounter = 1;
    int moduleId;
    private bool moduleSolved;

    void Awake()
    {
        moduleId = moduleIdCounter++;
        moduleSolved = false;
        foreach(KMSelectable obj in buttons){
            KMSelectable pressed = obj;
            pressed.OnInteract += delegate () { PressButton(pressed); return false; };
            pressed.OnInteractEnded += delegate () { ReleaseButton(pressed); };
        }
    }

    void Start() {
        clearPuzzleInit();
        clearPuzzleMain();
        generatePuzzle();
        //fillSolution(usedPuzzle);
    }

    void PressButton(KMSelectable pressed)
    {
        if(moduleSolved != true)
        {
            audio.PlayGameSoundAtTransform(KMSoundOverride.SoundEffect.ButtonPress, transform);
            if (pressed == buttons[82])
            {
                //Submit button
                pressed.AddInteractionPunch(0.25f);
                if (horizSelection.Equals(solutionH[usedPuzzle]) && vertSelection.Equals(solutionV[usedPuzzle]))
                {
                    moduleSolved = true;
                    success();
                    GetComponent<KMBombModule>().HandlePass();
                    //Start();
                }
                else
                {
                    StartCoroutine(wrong());
                    GetComponent<KMBombModule>().HandleStrike();
                }
            }
            else if (pressed == buttons[83])
            {
                //Clear button
                pressed.AddInteractionPunch(0.25f);
                runner = StartCoroutine(timer());
            }
            else
            {
                //If the grid button pressed was a vertical one then toggle it
                if (pressed.name.Contains("v"))
                {
                    int realind = Array.IndexOf(buttons, pressed) - 40;
                    if (vertSelection.ElementAt(realind).Equals('0'))
                    {
                        gridbuttons[Array.IndexOf(buttons, pressed)].GetComponent<Renderer>().enabled = true;
                        vertSelection = vertSelection.Insert(realind, "1");
                        vertSelection = vertSelection.Remove(realind + 1, 1);
                    }
                    else
                    {
                        gridbuttons[Array.IndexOf(buttons, pressed)].GetComponent<Renderer>().enabled = false;
                        vertSelection = vertSelection.Insert(realind, "0");
                        vertSelection = vertSelection.Remove(realind + 1, 1);
                    }
                }
                //If the grid button pressed was a horizontal one then toggle it
                else
                {
                    if (horizSelection.ElementAt(Array.IndexOf(buttons, pressed)).Equals('0'))
                    {
                        gridbuttons[Array.IndexOf(buttons, pressed)].GetComponent<Renderer>().enabled = true;
                        horizSelection = horizSelection.Insert(Array.IndexOf(buttons, pressed), "1");
                        horizSelection = horizSelection.Remove(Array.IndexOf(buttons, pressed) + 1, 1);
                    }
                    else
                    {
                        gridbuttons[Array.IndexOf(buttons, pressed)].GetComponent<Renderer>().enabled = false;
                        horizSelection = horizSelection.Insert(Array.IndexOf(buttons, pressed), "0");
                        horizSelection = horizSelection.Remove(Array.IndexOf(buttons, pressed) + 1, 1);
                    }
                }
            }
        }
    }

    void ReleaseButton(KMSelectable pressed)
    {
        if (moduleSolved != true)
        {
            if (pressed == buttons[83])
            {
                StopCoroutine(runner);
            }
        }
    }

    private void success()
    {
        for (int i = 0; i < gridbuttons.Length; i++)
        {
            if (gridbuttons[i].GetComponent<Renderer>().enabled)
            {
                gridbuttons[i].GetComponent<Renderer>().material = changeitems[2];
            }
        }
    }

    private IEnumerator wrong()
    {
        for (int i = 0; i < gridbuttons.Length; i++)
        {
            if (gridbuttons[i].GetComponent<Renderer>().enabled)
            {
                gridbuttons[i].GetComponent<Renderer>().material = changeitems[3];
            }
        }
        yield return new WaitForSeconds(1.0f);
        for (int i = 0; i < gridbuttons.Length; i++)
        {
            if (gridbuttons[i].GetComponent<Renderer>().enabled)
            {
                gridbuttons[i].GetComponent<Renderer>().material = changeitems[1];
            }
        }
        StopCoroutine("wrong");
    }

    //Debug method that fills the grid with the specified solution path (indexed starting from 0)
    private void fillSolution(int target)
    {
        for(int i = 0; i < solutionH[target].Length; i++)
        {
            if (solutionH[target].ElementAt(i).Equals('1'))
            {
                gridbuttons[i].GetComponent<Renderer>().enabled = true;
            }
        }
        for (int i = 0; i < solutionV[target].Length; i++)
        {
            if (solutionV[target].ElementAt(i).Equals('1'))
            {
                gridbuttons[i+40].GetComponent<Renderer>().enabled = true;
            }
        }
        horizSelection = solutionH[usedPuzzle];
        vertSelection = solutionV[usedPuzzle];
    }

    private void clearPuzzleInit()
    {
        //clears the puzzle of all already enabled dots
        for (int i = 0; i < whiteCircles.Length; i++)
        {
            whiteCircles[i].GetComponent<Renderer>().enabled = false;
        }
        for (int i = 0; i < whiteCircleRings.Length; i++)
        {
            whiteCircleRings[i].GetComponent<Renderer>().enabled = false;
        }
        for (int i = 0; i < blackCircles.Length; i++)
        {
            blackCircles[i].GetComponent<Renderer>().enabled = false;
        }
    }

    private IEnumerator timer()
    {
        timeHeld = 2;
        while (timeHeld < 3)
        {
            yield return new WaitForSeconds(0.01f);
            timeHeld += 0.01;
        }
        clearPuzzleMain();
        StopCoroutine(runner);
    }

    private void clearPuzzleMain()
    {
        //clears the puzzle of all already enabled paths
        for (int i = 0; i < gridbuttons.Length; i++)
        {
            gridbuttons[i].GetComponent<Renderer>().enabled = false;
        }
        horizSelection = "0000000000000000000000000000000000000000";
        vertSelection = "000000000000000000000000000000000000000000";
    }

    private void generatePuzzle()
    {
        //pick puzzle to use at random
        usedPuzzle = UnityEngine.Random.Range(0, dots.Length);
        //automatically fill in the dots depending on the picked puzzle
        for (int i = 0; i < dots[usedPuzzle].Length; i++)
        {
            if (dots[usedPuzzle].ElementAt(i).Equals('1'))
            {
                whiteCircles[i].GetComponent<Renderer>().enabled = true;
                whiteCircleRings[i].GetComponent<Renderer>().enabled = true;
            }
            else if (dots[usedPuzzle].ElementAt(i).Equals('2'))
            {
                blackCircles[i].GetComponent<Renderer>().enabled = true;
            }
        }
        Debug.LogFormat("[Masyu #{0}] The dots/circles are in the following pattern, where 0=blank,1=white,2=black...", moduleId);
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(0, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(6, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(12, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(18, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(24, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(30, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(36, 6));
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, dots[usedPuzzle].Substring(42, 6));
        Debug.LogFormat("[Masyu #{0}] The horizontal connections pattern should be in this order, where 0=blank connection and 1=filled connection", moduleId);
        Debug.LogFormat("[Masyu #{0}] (This should be read as the first character being the tl horizontal connector, and the last being the br horizontal connector with all inbetween being read in reading order)", moduleId);
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, solutionH[usedPuzzle]);
        Debug.LogFormat("[Masyu #{0}] The vertical connections pattern should be in this order, where 0=blank connection and 1=filled connection", moduleId);
        Debug.LogFormat("[Masyu #{0}] (This should be read as the first character being the tl vertical connector, and the last being the br vertical connector with all inbetween being read from top to bottom of each column from left to right)", moduleId);
        Debug.LogFormat("[Masyu #{0}] {1}", moduleId, solutionV[usedPuzzle]);
    }

    //twitch plays
    #pragma warning disable 414
    private readonly string TwitchHelpMessage = "!{0} a1 b1;d4 d5 [Toggles the edges between the sets of two specified cells] | !{0} h/horizontal 101100011 [Toggles the horizontal paths in reading order starting from the TL where 0=nopath & 1=path] | !{0} v/vertical 101100011 [Toggles the vertical paths in column order (top to bottom of each column from left to right) starting from the TL where 0=nopath & 1=path] | !{0} submit [Presses the submit button] | !{0} clear [Clears the board] \nAdditional commands are available. Use !{0} d/draw/t/trace help to learn more about draw and trace commands.";
    #pragma warning restore 414

    IEnumerator ProcessTwitchCommand(string command)
    {
        if (Regex.IsMatch(command, @"^\s*submit\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            yield return null;
            buttons[82].OnInteract();
            yield break;
        }
        if (Regex.IsMatch(command, @"^\s*clear\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            yield return null;
            buttons[83].OnInteract();
            yield return new WaitForSeconds(1.75f);
            buttons[83].OnInteractEnded();
            yield break;
        }
        string[] parameters2 = command.Split(' ');
        if (Regex.IsMatch(parameters2[0], @"^\s*h\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant) || Regex.IsMatch(parameters2[0], @"^\s*horizontal\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            if(parameters2.Length == 2)
            {
                if (parameters2[1].Length <= 40)
                {
                    for (int i = 0; i < parameters2[1].Length; i++)
                    {
                        if (!parameters2[1].ElementAt(i).Equals('0') && !parameters2[1].ElementAt(i).Equals('1'))
                        {
                            yield break;
                        }
                    }
                    yield return null;
                    for (int i = 0; i < parameters2[1].Length; i++)
                    {
                        if (parameters2[1].ElementAt(i).Equals('1') && horizSelection.ElementAt(i).Equals('0'))
                        {
                            buttons[i].OnInteract();
                        }else if (parameters2[1].ElementAt(i).Equals('0') && horizSelection.ElementAt(i).Equals('1'))
                        {
                            buttons[i].OnInteract();
                        }
                        yield return new WaitForSeconds(0.1f);
                    }
                    yield break;
                }
            }
        }
        if (Regex.IsMatch(parameters2[0], @"^\s*v\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant) || Regex.IsMatch(parameters2[0], @"^\s*vertical\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            if (parameters2.Length == 2)
            {
                if (parameters2[1].Length <= 42)
                {
                    for (int i = 0; i < parameters2[1].Length; i++)
                    {
                        if (!parameters2[1].ElementAt(i).Equals('0') && !parameters2[1].ElementAt(i).Equals('1'))
                        {
                            yield break;
                        }
                    }
                    yield return null;
                    for (int i = 0; i < parameters2[1].Length; i++)
                    {
                        if (parameters2[1].ElementAt(i).Equals('1') && vertSelection.ElementAt(i).Equals('0'))
                        {
                            buttons[i+40].OnInteract();
                        }
                        else if (parameters2[1].ElementAt(i).Equals('0') && vertSelection.ElementAt(i).Equals('1'))
                        {
                            buttons[i+40].OnInteract();
                        }
                        yield return new WaitForSeconds(0.1f);
                    }
                    yield break;
                }
            }
        }
        if (Regex.IsMatch(parameters2[0], @"(^\s*d\s*$)|(^\s*draw\s*$)", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            if (parameters2.Length == 2)
            {
                if (Regex.IsMatch(parameters2[1], @"^\s*help\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
                {
                    yield return "sendtochat !{1} d/draw a1 b1 b3 c3 c5 e5 a5 [Clears the screen and draws straight lines connecting between each point. Each adjacent coordinates must have a possible straight path between them (The last coordinate is considered adjacent to first coordinate.). At least 4 coordinates are expected. Separate each coordinate with spaces.]";
                }
                yield break;
            }

            //Limits the length of command
            if (parameters2.Length < 5 || parameters2.Length > 49)
                yield break;

            for (int i = 1; i < parameters2.Length; i++)
            {
                if (!Regex.IsMatch(parameters2[i], @"^\s*[a-f][1-8]\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
                    yield break;
                if (i != parameters2.Length - 1 ? !(parameters2[i][0] == parameters2[i + 1][0] ^ parameters2[i][1] == parameters2[i + 1][1]) : !(parameters2[1][0] == parameters2[i][0] ^ parameters2[1][1] == parameters2[i][1]))
                    yield break;
            }

            if (parameters2.GroupBy(x => x).Any(g => g.Count() > 1))
            {
                yield break;
            }

            yield return null;
            buttons[83].OnInteract();
            yield return new WaitForSeconds(1.75f);
            buttons[83].OnInteractEnded();

            for (int i = 1; i < parameters2.Length; i++)
            {
                bool isLast = i != parameters2.Length - 1;
                parameters2[i] = parameters2[i].ToLowerInvariant();
                if (isLast ? parameters2[i][1] == parameters2[i + 1][1] : parameters2[i][1] == parameters2[1][1])
                {
                    int initialIndex = (parameters2[i][1] - '1') * 5 + (parameters2[i][0] - 'a');
                    int times = isLast ? parameters2[i][0] - parameters2[i + 1][0] : parameters2[i][0] - parameters2[1][0];
                    for (int j = 0; j < Math.Abs(times); j++)
                    {
                        if (times > 0)
                            buttons[initialIndex - j - 1].OnInteract();
                        else
                            buttons[initialIndex + j].OnInteract();
                        yield return new WaitForSeconds(0.1f);
                    }
                }
                else if (isLast ? parameters2[i][0] == parameters2[i + 1][0] : parameters2[i][0] == parameters2[1][0])
                {
                    int initialIndex = (parameters2[i][1] - '1') + (parameters2[i][0] - 'a') * 7;
                    int times = isLast ? parameters2[i][1] - parameters2[i + 1][1] : parameters2[i][1] - parameters2[1][1];
                    for (int j = 0; j < Math.Abs(times); j++)
                    {
                        if (times > 0)
                            buttons[40 + initialIndex - j - 1].OnInteract();
                        else
                            buttons[40 + initialIndex + j].OnInteract();
                        yield return new WaitForSeconds(0.1f);
                    }
                }
                yield return "trycancel Drawing has been halted due to a request to cancel";
            }
            yield break;
        }
        if (Regex.IsMatch(parameters2[0], @"(^\s*t\s*$)|(^\s*trace\s*$)", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
        {
            if (parameters2.Length == 1)
                yield break;
            if (parameters2.Length == 2)
            {
                if (Regex.IsMatch(parameters2[1], @"^\s*help\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant))
                {
                    yield return "sendtochat !{1} t/trace a4 r u dlr c3 l [Traces path starting from the specified coordinates towards specified cardinal directions.The first arguments must be coordinate followed by cardinal directions.The command must end with cardinal direction.Separate each argument with spaces.]";
                }
                yield break;
            }
            bool firstParametersCoordinate = false;
            bool previousParametersIsDirection = false;

            for (int i = 1; i < parameters2.Length; i++)
            {
                if (Regex.IsMatch(parameters2[i], @"^\s*[a-f][1-8]\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant) && (!firstParametersCoordinate || previousParametersIsDirection))
                {
                    previousParametersIsDirection = false;
                    firstParametersCoordinate = true;
                }
                else if (Regex.IsMatch(parameters2[i], @"^\s*[udlr]+\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant) && firstParametersCoordinate)
                    previousParametersIsDirection = true;
                else
                    yield break;
            }

            if (!previousParametersIsDirection)
                yield break;

            string validatedCommand = parameters2.Join().ToLowerInvariant().Replace("trace", String.Empty).Replace("t", String.Empty).Replace(" ", String.Empty);

            //Limits the length of the command
            if (validatedCommand.Length > 75)
                yield break;

            yield return null;

            int[] coordinate = new int[2];
            //for (int i = 1; i < parameters2.Length; i++)
            for (int i = 0; i < validatedCommand.Length; i++)
            {
                if (i != validatedCommand.Length - 1 ? Regex.IsMatch(validatedCommand.Substring(i, 2), @"^\s*[a-f][1-8]\s*$", RegexOptions.IgnoreCase | RegexOptions.CultureInvariant) : false) 
                {
                    char[] letterCoordinate = validatedCommand.Substring(i, 2).ToCharArray();
                    coordinate[0] = letterCoordinate[0] - 'a';
                    coordinate[1] = letterCoordinate[1] - '1';
                    i++;
                }
                else if(validatedCommand[i] =='u' && coordinate[1] != 0)
                {
                    coordinate[1]--;
                    buttons[40 + coordinate[1] + coordinate[0] * 7].OnInteract();
                    yield return new WaitForSeconds(0.1f);
                }
                else if (validatedCommand[i] == 'd' && coordinate[1] != 7)
                {
                    buttons[40 + coordinate[1] + coordinate[0] * 7].OnInteract();
                    yield return new WaitForSeconds(0.1f);
                    coordinate[1]++;
                }
                else if (validatedCommand[i] == 'l' && coordinate[0] != 0)
                {
                    coordinate[0]--;
                    buttons[coordinate[1] * 5 + coordinate[0]].OnInteract();
                    yield return new WaitForSeconds(0.1f);
                }
                else if (validatedCommand[i] == 'r' && coordinate[0] != 5)
                {
                    buttons[coordinate[1] * 5 + coordinate[0]].OnInteract();
                    yield return new WaitForSeconds(0.1f);
                    coordinate[0]++;
                }
                yield return "trycancel Tracing has been halted due to a request to cancel";
            }
            yield break;
        }
        command = command.Replace(" ", String.Empty);
